package com.hibernate.dao;

import java.sql.Timestamp;
import java.util.ArrayList;
import java.util.Iterator;
import java.util.List;

import javax.servlet.http.HttpServletRequest;

import org.hibernate.Query;
import org.hibernate.SQLQuery;
import org.hibernate.Session;
import org.hibernate.Transaction;

import com.hibernate.support.HibernateSessionFactory;
import com.json.object.ShortwayRequestJSON;

public class ShortwayRequestDao {

	public List<ShortwayRequestJSON> SelectRequest(String phonenum) {
		// 创建session
		Session session = HibernateSessionFactory.getSession();
		List<ShortwayRequestJSON> shortwaylist = new ArrayList<ShortwayRequestJSON>();
		Transaction tx = session.beginTransaction();
		tx.begin();
		SQLQuery query = session
				.createSQLQuery("SELECT * FROM shortway_request where UserID=:phonenum ORDER BY ID DESC");
		query.setParameter("phonenum", phonenum);
		try {
			List<Object[]> list = query.list();
			tx.commit();
			Iterator<Object[]> iterator = list.iterator();
			while (iterator.hasNext()) {
				Object[] request = iterator.next();
				ShortwayRequestJSON item = new ShortwayRequestJSON();
				item.setId(request[0].toString());
				item.setUserId(request[1].toString());
				item.setRequestTime(request[2].toString());
				item.setUserRole(request[3].toString());
				item.setStartPlaceX(request[4].toString());
				item.setStartPlaceY(request[5].toString());
				item.setStartPlace(request[6].toString());
				item.setDestinationX(request[7].toString());
				item.setDestinationY(request[8].toString());
				item.setDestination(request[9].toString());
				item.setStartDate(request[10].toString());
				item.setStartTime(request[11].toString());
				item.setEndTime(request[12].toString());
				item.setDealStatus(request[13].toString());
				shortwaylist.add(item);
			}
		} catch (Exception e) {
			// TODO: handle exception
			e.printStackTrace();
			// 事务回滚
		} finally {
			// 关闭session
			session.close();
		}
		return shortwaylist;
	}

	public Boolean saveRequest(HttpServletRequest request) {
		// 创建session
		Session session = HibernateSessionFactory.getSession();
		// 开启事务
		Boolean success = true;
		Transaction tx = session.beginTransaction();
		Query query = session
				.createSQLQuery("INSERT INTO shortway_request(`UserID`, `RequestTime`, `UserRole`,`StartPlaceX`, `StartPlaceY`,`StartPlace`, `DestinationX`,`DestinationY`,`Destination`,`StartDate`, `StartTime`, `EndTime`,`DealStatus`) "
						+ "VALUES (:phonenum,:requesttime,:userrole,:startplacex,:startplacey,:startplace,:destinationx,:destinationy,:destination,:startdate,:starttime,:endtime,:status)");
		query.setParameter("phonenum", request.getParameter("phonenum"));
		query.setParameter("requesttime",
				new Timestamp(System.currentTimeMillis()));
		query.setParameter("userrole", request.getParameter("userrole"));
		query.setParameter("startplacex", request.getParameter("startplacex"));
		query.setParameter("startplacey", request.getParameter("startplacey"));
		query.setParameter("startplace", request.getParameter("startplace"));
		query.setParameter("destinationx", request.getParameter("destinationx"));
		query.setParameter("destinationy", request.getParameter("destinationy"));
		query.setParameter("destination", request.getParameter("destination"));
		query.setParameter("startdate", request.getParameter("startdate"));
		query.setParameter("starttime", request.getParameter("starttime"));
		query.setParameter("endtime", request.getParameter("endtime"));
		query.setParameter("status", 0);
		try {
			tx.begin();
			query.executeUpdate();
			// 提交事务
			tx.commit();
		} catch (Exception e) {
			// TODO: handle exception
			e.printStackTrace();
			// 事务回滚
			success = false;
			tx.rollback();
		} finally {
			// 关闭session
			session.close();
		}
		return success;
	}

	public Boolean deleteRequest(String phonenum, String time) {
		// 创建session
		Session session = HibernateSessionFactory.getSession();
		// 开启事务
		Boolean success = true;
		Transaction tx = session.beginTransaction();
		Query query = session
				.createSQLQuery("DELETE FROM shortway_request WHERE UserID=:phonenum and RequestTime=:time");
		query.setParameter("phonenum", phonenum);
		query.setParameter("time", time);
		try {
			tx.begin();
			if (query.executeUpdate() == 0)
				success = false;
			tx.commit();
		} catch (Exception e) {
			// TODO: handle exception
			e.printStackTrace();
			// 事务回滚
			success = false;
			tx.rollback();
		} finally {
			// 关闭session
			session.close();
		}
		return success;
	}

	public Boolean updateRequeststatue(String phonenum, Timestamp time,
			int status) {
		// 创建session.
		Session session = HibernateSessionFactory.getSession();
		// 开启事务
		Boolean success = true;
		Transaction tx = session.beginTransaction();
		Query query = session
				.createSQLQuery("UPDATE shortway_request SET DealStatus=:status WHERE UserID=:phonenum and RequestTime=:time");
		query.setParameter("phonenum", phonenum);
		query.setParameter("time", time);
		query.setParameter("status", status);
		try {
			tx.begin();
			query.executeUpdate();

			// 提交事务
			tx.commit();
		} catch (Exception e) {
			// TODO: handle exception
			e.printStackTrace();
			// 事务回滚
			success = false;
			tx.rollback();
		} finally {
			// 关闭session
			session.close();
		}
		return success;
	}
}
